let declineRequest=function(e){$.ajax({type:"get",url:$(e).prop("href"),success:function(t){let n=$(e)[0].parentElement;$(n).html('<button type="button" class="btn btn-outline-danger" disabled>Rejected</button>')},error:function(e){console.log(e.responseText)}})};$(".decline").click((function(e){e.preventDefault(),declineRequest($(this))}));let acceptRequest=function(e){$.ajax({type:"get",url:$(e).prop("href"),success:function(t){let n=$(e)[0].parentElement;$(n).html('<button type="button" class="btn btn-outline-success animate__animated animate__zoomIn animate__faster" disabled>Accepted</button>')},error:function(e){console.log(e.responseText)}})};$(".accept").click((function(e){e.preventDefault(),acceptRequest(this)}));